00060 template <typename T>
00061 bool TimerT<T>::isExpired() const
00062 {
00063     if (durationMs == 0)
00064     {
00065         return true;
00066     }
00067 
00068     T current_time = static_cast<T>(OS.now());
00069 
00070     // Handle timer overflow correctly using safer arithmetic
00071     if (current_time >= startMs)
00072     {
00073         // No overflow case - simple subtraction
00074         return (current_time - startMs) >= durationMs;
00075     }
00076     else
00077     {
00078         // Timer overflow occurred - calculate elapsed time correctly
00079         T max_value = static_cast<T>(~static_cast<T>(0));
00080 
00081         // Calculate elapsed time: time before overflow + time after overflow
00082         T elapsed_before_overflow = max_value - startMs + 1;
00083         T elapsed_after_overflow = current_time;
00084         T total_elapsed = elapsed_before_overflow + elapsed_after_overflow;
00085 
00086         // Check for overflow in the addition
00087         if (total_elapsed < elapsed_before_overflow)
00088         {
00089             // Addition overflowed, timer definitely expired
00090             return true;
00091         }
00092 
00093         return total_elapsed >= durationMs;
00094     }
00095 }
