00645 static void init_stack_canary()
00646 {
00647     extern char __bss_end;
00648     extern char *__brkval;
00649     // Current stack pointer approximation via address of a local
00650     uint8_t sp_probe;
00651     uint8_t *sp = (uint8_t *)&sp_probe;
00652 
00653     // Compute heap top
00654     uint8_t *heap_top = (uint8_t *)(__brkval ? __brkval : &__bss_end);
00655 
00656     // Safety margin to keep above the canary (below SP)
00657 #ifndef FSMOS_STACK_CANARY_MARGIN
00658 #define FSMOS_STACK_CANARY_MARGIN 32
00659 #endif
00660 
00661     // Region end just below current SP
00662     uint8_t *region_end = sp - FSMOS_STACK_CANARY_MARGIN;
00663 
00664     // Start from heap top - cover entire free RAM area
00665     uint8_t *region_start = heap_top;
00666 
00667     if (region_start >= region_end)
00668     {
00669         // Nothing to initialize; leave pointers null
00670         g_canary_start = g_canary_end = nullptr;
00671         return;
00672     }
00673 
00674     g_canary_start = region_start;
00675     g_canary_end   = region_end;
00676 
00677     // Disable interrupts around the fill to keep ISR stacks from interfering
00678     uint8_t sreg = SREG;
00679     cli();
00680     for (uint8_t *p = g_canary_start; p <= g_canary_end; ++p)
00681     {
00682         *p = STACK_CANARY_BYTE;
00683     }
00684     SREG = sreg; // restore interrupt state
00685 }
00686 
00687 static uint16_t measure_stack_used()
00688 {
00689     if (!g_canary_start || !g_canary_end || g_canary_start > g_canary_end)
00690     {
00691         return 0;
00692     }
00693     uint8_t *p = g_canary_start;
00694     while (p <= g_canary_end && *p == STACK_CANARY_BYTE)
00695     {
00696         ++p;
00697     }
00698     if (p > g_canary_end)
00699     {
00700         return 0;
00701     }
00702     // Bytes from first non-canary up to end are considered used
00703     return (uint16_t)(g_canary_end - p + 1);
00704 }
