00742 bool Scheduler::initializeTaskNodePool()
00743 {
00744     if (taskNodePoolInitialized)
00745     {
00746         return true;
00747     }
00748     // Preallocate TaskNode objects as a singly-linked free-list.
00749     // Start with the number of created tasks (at least 1).
00750     TaskNode *prev = nullptr;
00751     uint16_t initial = Task::getCreatedInstanceCount();
00752     if (initial == 0)
00753     {
00754         initial = 1;
00755     }
00756     for (uint8_t i = 0; i < initial; ++i)
00757     {
00758         TaskNode *node = (TaskNode *)malloc(sizeof(TaskNode));
00759         if (!node)
00760         {
00761             // Allocation failure; cleanup any already allocated nodes
00762             while (prev)
00763             {
00764                 TaskNode *temp = prev;
00765                 prev = prev->next;
00766                 free(temp);
00767             }
00768             taskNodePoolCapacity = 0;
00769             return false;
00770         }
00771         // Initialize fields manually; building free-list in reverse
00772         node->task = nullptr;
00773         node->next = prev;
00774         prev = node;
00775         taskNodePoolCapacity++;
00776     }
00777     freeTaskNodeHead = prev;
00778     taskNodePoolInitialized = true;
00779     return true;
00780 }
