00792     {
00793         logSystemEvent(LOG_ERROR, F("Task limit reached"));
00794         logInfof(F("Max tasks: %u, Current: %u"), MAX_TOPICS, taskCount);
00795         return false;
00796     }
00797 
00798     // Acquire node from pool using helper method
00799     TaskNode *newNode = allocateTaskNode(task);
00800     if (!newNode)
00801     {
00802         return false;  // Pool allocation failed
00803     }
00804 
00805     // Assign task ID
00806     task->taskId = nextTaskId++;
00807     if (nextTaskId == 0)
00808     {
00809         nextTaskId = 1;    // Avoid zero task ID
00810     }
00811 
00812     // Add to linked list (singly-linked)
00813     if (taskHead == nullptr)
00814     {
00815         // First task
00816         taskHead = taskTail = newNode;
00817     }
00818     else
00819     {
00820         // Append to tail
00821         taskTail->next = newNode;
00822         taskTail = newNode;
00823     }
00824 
00825     taskCount++;
00826     return true;
00827 }
00828 
00829 bool Scheduler::remove(Task *task)
00830 {
00831     if (!task)
00832     {
00833         return false;
00834     }
00835 
00836     // Find the node containing this task, tracking previous (singly-linked)
00837     TaskNode *current = taskHead;
00838     TaskNode *previous = nullptr;
00839     while (current != nullptr)
00840     {
00841         if (current->task == task)
00842         {
00843             // Remove from linked list
00844             if (previous)
00845             {
00846                 previous->next = current->next;
00847             }
00848             else
00849             {
00850                 // Removing head
00851                 taskHead = current->next;
00852             }
00853 
00854             if (current == taskTail)
00855             {
00856                 // Update tail if needed
00857                 taskTail = previous;
00858             }
00859 
00860             deallocateTaskNode(current);
00861             taskCount--;
00862             return true;
00863         }
00864         previous = current;
00865         current = current->next;
00866     }
00867 
00868     return false;
00869 }
00870 
00871 void Scheduler::removeAll()
00872 {
00873     TaskNode *current = taskHead;
00874     while (current != nullptr)
00875     {
00876         TaskNode *next = current->next;
00877         current->task->stop();
00878         deallocateTaskNode(current);
00879         current = next;
00880     }
00881     taskHead = taskTail = nullptr;
00882     taskCount = 0;
00883 }
