00894 void Scheduler::begin()
00895 {
00896     // Initialize stack canary before tasks start executing
00897 #if defined(__AVR__)
00898     init_stack_canary();
00899 #endif
00900     // Set log level to INFO for better debugging
00901     setLogLevel(LOG_INFO);
00902 
00903     // Log startup message
00904     logSystemEvent(LOG_INFO, F("FsmOS starting"));
00905 
00906     running = true;
00907     systemTime = millis();
00908 
00909 #if defined(__AVR__)
00910     init_stdio_to_serial();
00911 #endif
00912 
00913     // Start all tasks
00914     forEachTask([this](Task * task)
00915     {
00916         task->start();
00917         feedWatchdog();
00918     });
00919 
00920     // Log task count
00921     logSystemEvent(LOG_INFO, F("Scheduler ready"));
