00924 void Scheduler::loopOnce()
00925 {
00926     if (!running)
00927     {
00928         return;
00929     }
00930 
00931     updateSystemTime();
00932 
00933     // Decrease remaining time for all active tasks
00934     forEachTask([](Task * task)
00935     {
00936         if (task->isActive() && task->remainingTime > 0)
00937         {
00938             task->remainingTime--;
00939         }
00940     });
00941 
00942     // Feed watchdog timer
00943     feedWatchdog();
00944 
00945     // Process a limited number of queued messages per tick
00946     processMessages();
00947 
00948     Task *next_task = findNextTask();
00949     if (next_task)
00950     {
00951         executeTask(next_task);
00952     }
00953 }
00954 
00955 void Scheduler::loop()
00956 {
00957     while (running)
00958     {
00959         loopOnce();
00960     }
00961 }
