00990 uint16_t Scheduler::getFreeMemory() const
00991 {
00992     extern char __heap_start;      // start of heap (in .bss)
00993     extern char *__brkval;         // current heap break (nullptr if none)
00994     char v;                        // stack variable to get current stack address
00995     // Difference between current stack pointer and heap end/start
00996     return (uint16_t)(&v - (__brkval == 0 ? &__heap_start : __brkval));
00997 }
