01077 void Scheduler::processMessages()
01078 {
01079     while (true)
01080     {
01081         // Dequeue node to ensure we recycle only after handler finishes
01082         if (msgCount == 0)
01083         {
01084             break;
01085         }
01086         MsgNode *node = msgHead;
01087         msgHead = node->next;
01088         if (!msgHead)
01089         {
01090             msgTail = nullptr;
01091         }
01092         msgCount--;
01093 
01094         QueuedMessage &qm = node->payload;
01095         Task *target = getTask(qm.targetTaskId);
01096         if (target && target->isActive())
01097         {
01098             target->on_msg(qm.msg);
01099         }
01100 
01101         // Recycle node after handler returns; keep buffer for reuse
01102         node->next = freeHead;
01103         freeHead = node;
01104     }
01105 }
01106 
01107 void Scheduler::updateSystemTime() { systemTime = millis(); }
01108 
01109 Task *Scheduler::findNextTask()
01110 {
01111     Task *nextTask = nullptr;
01112     uint16_t shortestTime = UINT16_MAX;
01113 
01114     TaskNode *current = taskHead;
01115     while (current != nullptr)
01116     {
01117         if (current->task && current->task->isActive() && current->task->remainingTime == 0)
01118         {
01119             // If task declares a message production budget, ensure enough free queue slots
01120             uint8_t budget = current->task->getMaxMessageBudget();
01121             // Only use default if task hasn't explicitly set a budget (inherited default)
01122             if (budget == DEFAULT_TASK_MESSAGE_BUDGET)
01123             {
01124                 // Task is using the default, which is fine
01125             }
01126             // If budget is 0, task explicitly wants no message production
01127             // If budget > 0, task has explicit budget
01128             // In both cases, respect the task's decision
01129             if (getFreeQueueSlots() < budget)
01130             {
01131                 // Skip this task for now; not enough capacity to accept its messages
01132             }
01133             else if (nextTask == nullptr)
01134             {
01135                 nextTask = current->task;
01136             }
01137             else if (current->task->getPriority() > nextTask->getPriority())
01138             {
01139                 // Higher enum value wins (PRIORITY_SYSTEM=7 > PRIORITY_LOWEST=0)
01140                 nextTask = current->task;
01141             }
01142             else if (current->task->getPriority() == nextTask->getPriority())
01143             {
01144                 // Same priority, smaller task ID wins
01145                 if (current->task->getId() < nextTask->getId())
01146                 {
01147                     nextTask = current->task;
01148                 }
01149             }
01150         }
01151         else if (current->task && current->task->isActive() && current->task->remainingTime < shortestTime)
01152         {
01153             shortestTime = current->task->remainingTime;
01154         }
01155         current = current->next;
01156     }
01157 
01158     return nextTask;
01159 }
01160 
01161 void Scheduler::executeTask(Task *task)
01162 {
01163     if (!task || !task->isActive())
01164     {
01165         return;
01166     }
01167 
01168     uint32_t execStart = micros();
01169     uint32_t currentTime = systemTime;
01170 
01171     // Handle task timing monitoring
01172     handleTaskTiming(task, currentTime);
01173 
01174     // Execute the actual task step
01175     executeTaskStep(task);
01176 
01177     // Update task execution statistics
01178     updateTaskStatistics(task, execStart);
01179 
01180     // Update timing monitoring variables
01181     updateTimingVariables(task);
01182 
01183     // Check for terminated tasks
01184     checkForTerminatedTask(task);
01185 }
01186 
01187 // Refactored helper methods for executeTask
01188 void Scheduler::handleTaskTiming(Task *task, uint32_t currentTime)
01189 {
01190     // Set scheduled time (when task should have started)
01191     task->scheduledTime = currentTime - task->getPeriod();
01192     task->actualStartTime = currentTime;
01193 
01194     // Check for delay
01195     if (task->actualStartTime > task->scheduledTime)
01196     {
01197         uint32_t delay = task->actualStartTime - task->scheduledTime;
01198         uint16_t delayMs = (delay > 65535) ? 65535 : static_cast<uint16_t>(delay);
01199 
01200         // Update delay statistics
01201         task->delayCount++;
01202         if (delayMs > task->maxDelayMs)
01203         {
01204             task->maxDelayMs = delayMs;
01205         }
01206 
01207         // Log delay with attribution
01208         logTaskDelay(task, delayMs, lastExecutedTaskId);
01209     }
01210 }
01211 
01212 void Scheduler::executeTaskStep(Task *task)
01213 {
01214     // Reset remaining time for next execution
01215     task->remainingTime = task->getPeriod();
01216 
01217     // Execute task step
01218     task->step();
01219 }
01220 
01221 void Scheduler::updateTaskStatistics(Task *task, uint32_t execStart)
01222 {
01223     // Calculate execution time
01224     uint32_t execTime = micros() - execStart;
01225     uint16_t execTime16 = (execTime > 65535) ? 65535 : static_cast<uint16_t>(execTime);
01226 
01227     // Update run count
01228     if (task->runCount < 65535)
01229     {
01230         task->runCount++;
01231     }
01232 
01233     // Update max execution time
01234     if (execTime16 > task->maxExecTimeUs)
01235     {
01236         task->maxExecTimeUs = execTime16;
01237     }
01238 
01239     // Update average execution time
01240     if (task->runCount == 1)
01241     {
01242         task->avgExecTimeUs = execTime16;
01243     }
01244     else if (task->runCount == 65535)
01245     {
01246         // Exponential moving average to avoid overflow
01247         uint32_t diff = execTime16 - task->avgExecTimeUs;
01248         int32_t adjustment = diff / 1000;  // Slow adaptation
01249         int32_t newAvg = task->avgExecTimeUs + adjustment;
01250 
01251         // Clamp to valid range
01252         if (newAvg < 0)
01253         {
01254             newAvg = 0;
01255         }
01256         if (newAvg > 65535)
01257         {
01258             newAvg = 65535;
01259         }
01260 
01261         task->avgExecTimeUs = static_cast<uint16_t>(newAvg);
01262     }
01263     else
01264     {
01265         // Simple moving average
01266         uint32_t newAvg = ((uint32_t)task->avgExecTimeUs * (task->runCount - 1) + execTime16) / task->runCount;
01267         task->avgExecTimeUs = (newAvg > 65535) ? 65535 : static_cast<uint16_t>(newAvg);
01268     }
01269 }
