01327 // Memory management helpers
01328 TaskNode *Scheduler::allocateTaskNode(Task *task)
01329 {
01330     if (!taskNodePoolInitialized && !initializeTaskNodePool())
01331     {
01332         return nullptr;
01333     }
01334     if (!freeTaskNodeHead)
01335     {
01336         // Try to expand by one
01337         TaskNode *node = (TaskNode *)malloc(sizeof(TaskNode));
01338         if (!node)
01339         {
01340             return nullptr;
01341         }
01342         node->task = nullptr;
01343         node->next = nullptr;
01344         freeTaskNodeHead = node;
01345         taskNodePoolCapacity++;
01346     }
01347     TaskNode *node = freeTaskNodeHead;
01348     freeTaskNodeHead = freeTaskNodeHead->next;
01349     node->task = task;
01350     node->next = nullptr;
01351     return node;
01352 }
01353 
01354 void Scheduler::deallocateTaskNode(TaskNode *node)
01355 {
01356     if (!node)
01357     {
01358         return;
01359     }
01360     node->task = nullptr;
01361     node->next = freeTaskNodeHead;
01362     freeTaskNodeHead = node;
01363 }
01364 
01365 Scheduler::MsgNode *Scheduler::allocateMsgNode()
01366 {
01367     if (!freeHead)
01368     {
01369         if (!allocateMsgNodesChunk())
01370         {
01371             return nullptr;
01372         }
01373     }
01374 
01375     MsgNode *node = freeHead;
01376     freeHead = freeHead->next;
01377     node->next = nullptr;
01378     return node;
01379 }
01380 
01381 void Scheduler::deallocateMsgNode(Scheduler::MsgNode *node)
01382 {
01383     if (!node)
01384     {
01385         return;
01386     }
01387 
01388     // Free the buffer if it exists
01389     if (node->payload.buffer)
01390     {
01391         free(node->payload.buffer);
01392         node->payload.buffer = nullptr;
01393         node->payload.capacity = 0;
01394     }
01395 
01396     // Reset message data
01397     node->payload.targetTaskId = 0;
01398     node->payload.msg.type = 0;
01399     node->payload.msg.topic = 0;
01400     node->payload.msg.arg = 0;
01401     node->payload.msg.refCount = 0;
01402 
01403     // Return to free list
01404     node->next = freeHead;
01405     freeHead = node;
01406 }
