01437 ResetCause Scheduler::getResetCause()
01438 {
01439     uint8_t flags = getResetCauseFlags();
01440 
01441     if (flags == 0)
01442     {
01443         return RESET_UNKNOWN;
01444     }
01445 
01446     // Check for multiple causes
01447     uint8_t count = 0;
01448     if (flags & RESET_CAUSE_POWER_ON)
01449     {
01450         count++;
01451     }
01452     if (flags & RESET_CAUSE_EXTERNAL)
01453     {
01454         count++;
01455     }
01456     if (flags & RESET_CAUSE_BROWN_OUT)
01457     {
01458         count++;
01459     }
01460     if (flags & RESET_CAUSE_WATCHDOG)
01461     {
01462         count++;
01463     }
01464 
01465     if (count > 1)
01466     {
01467         return RESET_MULTIPLE;
01468     }
01469 
01470     // Single cause
01471     if (flags & RESET_CAUSE_POWER_ON)
01472     {
01473         return RESET_POWER_ON;
01474     }
01475     if (flags & RESET_CAUSE_EXTERNAL)
01476     {
01477         return RESET_EXTERNAL;
01478     }
01479     if (flags & RESET_CAUSE_BROWN_OUT)
01480     {
01481         return RESET_BROWN_OUT;
01482     }
01483     if (flags & RESET_CAUSE_WATCHDOG)
01484     {
01485         return RESET_WATCHDOG;
01486     }
01487 
01488     return RESET_UNKNOWN;
01489 }
