01524 bool Scheduler::getSystemMemoryInfo(SystemMemoryInfo &info)
01525 {
01526     info.freeRam = getFreeMemory();
01527     info.totalRam = 2048;    // AVR typical
01528     // Heap size (bytes) from end of .bss to current break value (if any)
01529 #if defined(__AVR__)
01530     extern char __bss_end;
01531     extern char *__brkval;
01532     if (__brkval)
01533     {
01534         info.heapSize = (uint16_t)((uint16_t)__brkval - (uint16_t)&__bss_end);
01535     }
01536     else
01537     {
01538         info.heapSize = 0;
01539     }
01540 #else
01541     info.heapSize = 0;
01542 #endif
01543     info.largestBlock = 0;   // Not tracked
01544     info.heapFragments = 0;  // Not tracked
01545 #if defined(__AVR__)
01546     // Approximate stack usage based on canary region
01547     extern char __bss_end;
01548     extern char *__brkval;
01549     uint16_t lower = (uint16_t)(__brkval ? __brkval : &__bss_end);
01550     uint16_t upper = (uint16_t)RAMEND;
01551     uint16_t approxUsed = measure_stack_used();
01552     extern uint8_t *g_canary_start; extern uint8_t *g_canary_end;
01553     uint16_t windowSize = 0;
01554     if (g_canary_start && g_canary_end && g_canary_end >= g_canary_start)
01555     {
01556         windowSize = (uint16_t)(g_canary_end - g_canary_start + 1);
01557     }
01558     else
01559     {
01560         windowSize = (upper >= lower) ? (upper - lower + 1) : 0;
01561     }
01562     info.stackSize = windowSize;
01563     info.stackUsed = (approxUsed > windowSize) ? windowSize : approxUsed;
01564     info.stackFree = (info.stackUsed >= info.stackSize) ? 0 : (info.stackSize - info.stackUsed);
01565 #else
01566     info.stackSize = 0;
01567     info.stackUsed = 0;
01568     info.stackFree = 0;
01569 #endif
01570     info.totalTasks = taskCount;
01571     // Sum estimated task memory (struct size + subscription bitfield)
01572     uint16_t taskMem = 0;
01573     TaskNode *tn = taskHead;
01574     while (tn)
01575     {
01576         if (tn->task)
01577         {
01578             taskMem += tn->task->getTaskStructSize();
01579             taskMem += sizeof(tn->task->subscribedTopics);
01580         }
01581         tn = tn->next;
01582     }
01583     info.taskMemory = taskMem;
01584     info.activeMessages = msgCount;  // Provide actual active message count
01585     // Approximate message memory: nodes + buffers across queue and free lists
01586     uint16_t msgMem = 0;
01587     MsgNode *mn = msgHead;
01588     while (mn)
01589     {
01590         msgMem += sizeof(MsgNode);
01591         msgMem += mn->payload.capacity;
01592         mn = mn->next;
01593     }
01594     mn = freeHead;
01595     while (mn)
01596     {
01597         msgMem += sizeof(MsgNode);
01598         msgMem += mn->payload.capacity;
01599         mn = mn->next;
01600     }
01601     info.messageMemory = msgMem;
01602 
01603     // Flash usage from build-time constants
01604 #ifdef BUILD_FLASH_USED
01605     // Use real build-time values injected by build script
01606     info.flashUsed = BUILD_FLASH_USED;
01607     info.flashFree = BUILD_FLASH_FREE;
01608 #else
01609     info.flashUsed = 0;       // Not available at runtime
01610     info.flashFree = 0;       // Not available at runtime
01611 #endif
01612 
01613     // EEPROM usage (approximate based on known usage)
01614 #ifdef FSMOS_EEPROM_SIZE
01615     // Estimate EEPROM usage based on known variables stored
01616     // LightTask saves dim level (1 byte), other tasks may use EEPROM
01617     uint16_t eepromUsed = 1;  // Conservative estimate
01618     info.eepromUsed = eepromUsed;
01619     info.eepromFree = FSMOS_EEPROM_SIZE - eepromUsed;
01620 #else
01621     info.eepromUsed = 0;      // Not available at runtime
01622     info.eepromFree = 0;      // Not available at runtime
01623 #endif
01624     return true;
01625 }
01626 
01627 bool Scheduler::getTaskMemoryInfo(uint8_t task_id, TaskMemoryInfo &info)
01628 {
01629     Task *task = getTask(task_id);
01630     if (!task)
01631     {
01632         return false;
01633     }
01634 
01635     info.task_id = task->getId();
01636     // Derive struct size via virtual API; if not implemented by a task, it won't link.
01637     info.task_struct_size = task->getTaskStructSize();
01638     info.subscription_size = sizeof(task->subscribedTopics);
01639     info.queue_size = 0;         // No per-task queue in current design
01640     info.total_allocated = info.task_struct_size + info.subscription_size;
01641     return true;
01642 }
