01657 static void printLogHeader(Scheduler::LogLevel level)
01658 {
01659 #if defined(__AVR__)
01660     const char *level_str = "DEBUG";
01661     switch (level)
01662     {
01663         case Scheduler::LOG_INFO:
01664             level_str = "INFO";
01665             break;
01666         case Scheduler::LOG_WARN:
01667             level_str = "WARN";
01668             break;
01669         case Scheduler::LOG_ERROR:
01670             level_str = "ERROR";
01671             break;
01672         default:
01673             break;
01674     }
01675     Serial.print(F("["));
01676     Serial.print(level_str);
01677     Serial.print(F("] "));
01678 #else
01679     (void)level;
01680 #endif
01681 }
