01749 uint8_t Scheduler::getFreeQueueSlots() const
01750 {
01751     return static_cast<uint8_t>(MAX_MESSAGE_POOL_SIZE - msgCount);
01752 }
01753 
01754 uint8_t Scheduler::getMostDelayingTask() const
01755 {
01756     uint8_t mostDelayingTaskId = 0;
01757     uint16_t maxDelayCount = 0;
01758 
01759     TaskNode *current = taskHead;
01760     while (current != nullptr)
01761     {
01762         if (current->task && current->task->getDelayCount() > maxDelayCount)
01763         {
01764             maxDelayCount = current->task->getDelayCount();
01765             mostDelayingTaskId = current->task->getId();
01766         }
01767         current = current->next;
01768     }
01769 
01770     return mostDelayingTaskId;
01771 }
01772 
01773 bool Scheduler::allocateMsgNodesChunk()
01774 {
01775     if (totalNodes >= MAX_MESSAGE_POOL_SIZE)
01776     {
01777         return false;
01778     }
01779 
01780     // Allocate up to 4 nodes, but dont exceed hard cap
01781     uint8_t canAdd = static_cast<uint8_t>(MAX_MESSAGE_POOL_SIZE - totalNodes);
01782     uint8_t toAdd = (canAdd >= 4) ? 4 : canAdd;
01783     for (uint8_t i = 0; i < toAdd; i++)
01784     {
01785         MsgNode *n = (MsgNode *)malloc(sizeof(MsgNode));
01786         if (!n)
01787         {
01788             // Allocation failed; stop early
01789             break;
01790         }
01791         // Initialize node payload to safe defaults
01792         n->next = freeHead;
01793         n->payload.targetTaskId = 0;
01794         n->payload.msg.type = 0;
01795         n->payload.msg.topic = 0;
01796         n->payload.msg.arg = 0;
01797         n->payload.msg.refCount = 0;
01798         n->payload.buffer = nullptr;
01799         n->payload.capacity = 0;
01800         freeHead = n;
01801         totalNodes++;
01802     }
01803     return true;
01804 }
01805 
01806 bool Scheduler::enqueueQueuedMessage(uint8_t targetTaskId, uint8_t topic, uint8_t type, uint16_t arg)
01807 {
01808     if (msgCount >= MAX_MESSAGE_POOL_SIZE)
01809     {
01810         return false;
01811     }
01812 
01813     if (!freeHead)
01814     {
01815         allocateMsgNodesChunk();
01816         if (!freeHead)
01817         {
01818             return false;
01819         }
01820     }
01821 
01822     MsgNode *node = freeHead;
01823     freeHead = freeHead->next;
01824 
01825     QueuedMessage &slot = node->payload;
01826     slot.targetTaskId = targetTaskId;
01827     slot.msg.type = type;
01828     slot.msg.topic = topic;
01829     slot.msg.arg = arg;
01830     slot.msg.refCount = 0;
01831 
01832     node->next = nullptr;
01833     if (msgTail)
01834     {
01835         msgTail->next = node;
01836         msgTail = node;
01837     }
01838     else
01839     {
01840         msgHead = msgTail = node;
01841     }
01842     msgCount++;
01843     return true;
01844 }
01845 
01846 bool Scheduler::dequeueQueuedMessage(QueuedMessage &out)
01847 {
01848     if (msgCount == 0)
01849     {
01850         return false;
01851     }
01852     MsgNode *node = msgHead;
01853     msgHead = node->next;
01854     if (!msgHead)
01855     {
01856         msgTail = nullptr;
01857     }
01858     out = node->payload;
01859     // Node is not recycled here to avoid in-flight overwrite; caller should recycle
01860     msgCount--;
01861     return true;
01862 }
