00884     // Message handling
00890     void subscribe(uint8_t topic)
00891     {
00892         if (topic < MAX_TOPICS)
00893         {
00894             subscribedTopics |= (static_cast<TopicBitfield>(1) << topic);
00895         }
00896     }
00897 
00902     void unsubscribe(uint8_t topic)
00903     {
00904         if (topic < MAX_TOPICS)
00905         {
00906             subscribedTopics &= ~(static_cast<TopicBitfield>(1) << topic);
00907         }
00908     }
00909 
00915     bool isSubscribedToTopic(uint8_t topic) const
00916     {
00917         if (topic >= MAX_TOPICS)
00918         {
00919             return false;
00920         }
00921         return (subscribedTopics & (static_cast<TopicBitfield>(1) << topic)) != 0;
00922     }
00923 
00928     uint8_t getTopicCount() const
00929     {
00930 #if TOPIC_BITFIELD_SIZE <= 8
00931         return __builtin_popcount(static_cast<uint8_t>(subscribedTopics));
00932 #elif TOPIC_BITFIELD_SIZE <= 16
00933         return __builtin_popcount(static_cast<uint16_t>(subscribedTopics));
00934 #else
00935         return __builtin_popcountl(static_cast<unsigned long>(subscribedTopics));
00936 #endif
00937     }
